package lexer

import (
	"strings"
	"unicode"

	"github.com/PaulioRandall/voodoo-go/shared"
	"github.com/PaulioRandall/voodoo-go/symbol"
)

// ScanLine scans a line and creates an array of symbols based on
// the grammer rules of the language. If the line is empty then an
// empty array wwill be returned. If the line contains whitespace
// only a single symbol with the whitespace type is returned.
// No panic is generated by the scanner so if a panic occurs it's
// either a system issue or a bug.
func ScanLine(line string, lineNum int) (r []symbol.Symbol, lxErr LexError) {

	if line == `` {
		r = []symbol.Symbol{}
		return
	}

	itr := shared.NewRuneItr(line)

	for itr.HasNext() {
		var s *symbol.Symbol

		switch {
		case itr.IsNextLetter():
			s = wordSym(itr)
		case itr.IsNextDigit():
			s, lxErr = numSym(itr)
		case itr.IsNextSpace():
			s = spaceSym(itr)
		case itr.IsNext('@'):
			s, lxErr = sourcerySym(itr)
		case itr.IsNext('"'):
			s, lxErr = strSym(itr)
		case itr.IsNextStr(`//`):
			s = commentSym(itr)
		default:
			s, lxErr = otherSym(itr)
		}

		if lxErr != nil {
			lxErr.Line(lineNum)
			r = nil
			break
		}

		s.Line = lineNum
		r = append(r, *s)
	}

	return
}

// wordSym handles symbols that start with a unicode category L rune.
// I.e. a letter from any alphabet, a word may resolve into a:
// - variable name
// - keyword
// - boolean value (`true` or `false`)
func wordSym(itr *shared.RuneItr) *symbol.Symbol {

	start := itr.Index()
	str := extractWordStr(itr)
	symType := symbol.UNDEFINED

	switch strings.ToLower(str) {
	case `scroll`:
		symType = symbol.KEYWORD_SCROLL
	case `spell`:
		symType = symbol.KEYWORD_SPELL
	case `loop`:
		symType = symbol.KEYWORD_LOOP
	case `when`:
		symType = symbol.KEYWORD_WHEN
	case `end`:
		symType = symbol.KEYWORD_END
	case `key`:
		symType = symbol.KEYWORD_KEY
	case `val`:
		symType = symbol.KEYWORD_VAL
	case `true`:
		symType = symbol.BOOLEAN
	case `false`:
		symType = symbol.BOOLEAN
	default:
		symType = symbol.VARIABLE
	}

	return &symbol.Symbol{
		Val:   str,
		Start: start,
		End:   itr.Index(),
		Type:  symType,
	}
}

// numSym handles symbols that start with a unicode category Nd rune.
// I.e. any number from 0 to 9, a number may resolve into a:
// - literal number
func numSym(itr *shared.RuneItr) (s *symbol.Symbol, err LexError) {

	start := itr.Index()
	n, err := extractNum(itr)
	if err != nil {
		return
	}

	s = &symbol.Symbol{
		Val:   n,
		Start: start,
		End:   itr.Index(),
		Type:  symbol.NUMBER,
	}

	return
}

// extractNum extracts a number, as a string, from the supplied
// iterator.
func extractNum(itr *shared.RuneItr) (string, LexError) {
	sb := strings.Builder{}

	for itr.HasNext() {
		if itr.IsNextStr(`..`) {
			break
		}

		if itr.IsNext('.') {
			sb.WriteRune(itr.NextRune())
			return extractFractional(itr, &sb)
		}

		if itr.IsNextDigit() || itr.IsNext('_') {
			sb.WriteRune(itr.NextRune())
		} else {
			break
		}
	}

	return sb.String(), nil
}

// extractFractional extracts the fractional part of a number,
// as a string, from the supplied iterator and returns the
// number including the integer part.
func extractFractional(itr *shared.RuneItr, sb *strings.Builder) (string, LexError) {
	for itr.HasNext() {
		if itr.IsNext('.') {
			m := "Numbers can't have two fractional parts"
			return "", NewLexError(m, itr.Index())
		}

		if itr.IsNextDigit() || itr.IsNext('_') {
			sb.WriteRune(itr.NextRune())
		} else {
			break
		}
	}

	return sb.String(), nil
}

// spaceSym handles symbols that start with a rune with the
// unicode whitespace property.
// I.e. any whitespace rune, whitespace may resolve into a:
// - meaningless symbol that can be ignored when parsing
func spaceSym(itr *shared.RuneItr) *symbol.Symbol {

	start := itr.Index()
	sb := strings.Builder{}

	for itr.HasNext() {
		if !itr.IsNextSpace() {
			break
		}
		sb.WriteRune(itr.NextRune())
	}

	return &symbol.Symbol{
		Val:   sb.String(),
		Start: start,
		End:   itr.Index(),
		Type:  symbol.WHITESPACE,
	}
}

// sourcerySym handles symbols that start with a at sign rune `@`.
// Sourcery symbols may resolve into a:
// - go function call
func sourcerySym(itr *shared.RuneItr) (s *symbol.Symbol, err LexError) {

	if !unicode.IsLetter(itr.PeekRelRune(1)) {
		m := "Expected first rune after `@` to be a letter"
		err = NewLexError(m, itr.Index())
		return
	}

	start := itr.Index()
	firstLetter := string(itr.NextRune())
	val := firstLetter + extractWordStr(itr)

	s = &symbol.Symbol{
		Val:   val,
		Start: start,
		End:   itr.Index(),
		Type:  symbol.SOURCERY,
	}

	return
}

// strSym handles symbols that start with the double quote `"` rune.
// Quoted strings may resolve into a:
// - string literal
func strSym(itr *shared.RuneItr) (s *symbol.Symbol, err LexError) {

	start := itr.Index()
	closed, str := extractStr(itr)

	if !closed {
		m := "Did someone forget to close a string literal?!"
		err = NewLexError(m, itr.Index())
		return
	}

	s = &symbol.Symbol{
		Val:   str,
		Start: start,
		End:   itr.Index(),
		Type:  symbol.STRING,
	}

	return
}

// extractStr extracts a string literal from a string iterator
// returning true if the last rune was escaped.
func extractStr(itr *shared.RuneItr) (closed bool, s string) {

	sb := strings.Builder{}
	sb.WriteRune(itr.NextRune())
	isEscaped := false

	for itr.HasNext() {
		ru := itr.NextRune()
		sb.WriteRune(ru)

		if !isEscaped && ru == '"' {
			closed = true
			break
		}

		if ru == '\\' {
			isEscaped = !isEscaped
		} else {
			isEscaped = false
		}
	}

	s = sb.String()
	return
}

// commentSym handles symbols that start with two forward slashes
// `//`. Double forward slashes may resolve into a:
// - comment
func commentSym(itr *shared.RuneItr) *symbol.Symbol {

	start := itr.Index()
	str := itr.RemainingStr()

	return &symbol.Symbol{
		Val:   str,
		Start: start,
		End:   itr.Index(),
		Type:  symbol.COMMENT,
	}
}

// otherSym handles any symbols that don't have a specific handling
// function. These symbols may resolve into a:
// - operator, 1 or 2 runes including truthy and not
// - code block start or end, i.e. bracket
// - value separator, i.e. comma
// - key-value separator, i.e. colon
// - void value, i.e. underscore
func otherSym(itr *shared.RuneItr) (s *symbol.Symbol, err LexError) {

	start := itr.Index()
	symType := symbol.UNDEFINED
	runeCount := 0

	set := func(t symbol.SymbolType, totalRunes int) {
		symType = t
		runeCount = totalRunes
	}

	switch {
	case itr.IsNextStr(`<-`):
		set(symbol.ASSIGNMENT, 2)
	case itr.IsNextStr(`<=`):
		set(symbol.LESS_THAN_OR_EQUAL, 2)
	case itr.IsNext('<'):
		set(symbol.LESS_THAN, 1)
	case itr.IsNextStr(`>=`):
		set(symbol.GREATER_THAN_OR_EQUAL, 2)
	case itr.IsNext('>'):
		set(symbol.GREATER_THAN, 1)
	case itr.IsNextStr(`==`):
		set(symbol.EQUAL, 2)
	case itr.IsNextStr(`=>`):
		set(symbol.IF_TRUE_THEN, 2)
	case itr.IsNextStr(`!=`):
		set(symbol.NOT_EQUAL, 2)
	case itr.IsNext('!'):
		set(symbol.NEGATION, 1)
	case itr.IsNextStr(`||`):
		set(symbol.OR, 2)
	case itr.IsNextStr(`&&`):
		set(symbol.AND, 2)
	case itr.IsNext('+'):
		set(symbol.ADD, 1)
	case itr.IsNext('-'):
		set(symbol.SUBTRACT, 1)
	case itr.IsNext('*'):
		set(symbol.MULTIPLY, 1)
	case itr.IsNext('/'):
		set(symbol.DIVIDE, 1)
	case itr.IsNext('%'):
		set(symbol.MODULO, 1)
	case itr.IsNext('('):
		set(symbol.CURVED_BRACE_OPEN, 1)
	case itr.IsNext(')'):
		set(symbol.CURVED_BRACE_CLOSE, 1)
	case itr.IsNext('['):
		set(symbol.SQUARE_BRACE_OPEN, 1)
	case itr.IsNext(']'):
		set(symbol.SQUARE_BRACE_CLOSE, 1)
	case itr.IsNext(','):
		set(symbol.VALUE_SEPARATOR, 1)
	case itr.IsNext(':'):
		set(symbol.KEY_VALUE_SEPARATOR, 1)
	case itr.IsNextStr(`..`):
		set(symbol.RANGE, 2)
	case itr.IsNext('_'):
		set(symbol.VOID, 1)
	default:
		ru := itr.NextRune()
		m := "I don't know what this symbol means '" + string(ru) + "'"
		err = NewLexError(m, itr.Index())
		return
	}

	str, e := itr.NextStr(runeCount)
	if e != nil {
		err = NewLexError(err.Error(), itr.Index())
		return
	}

	s = &symbol.Symbol{
		Val:   str,
		Start: start,
		End:   itr.Index(),
		Type:  symType,
	}

	return
}

// extractWordStr iterates a rune iterator until a single word has
// been extracted retruning the string.
func extractWordStr(itr *shared.RuneItr) string {
	sb := strings.Builder{}

	for itr.HasNext() {
		switch {
		case itr.IsNextLetter():
			fallthrough
		case itr.IsNextDigit():
			fallthrough
		case itr.IsNext('_'):
			sb.WriteRune(itr.NextRune())
		default:
			return sb.String()
		}
	}

	return sb.String()
}
